        private readonly BigInteger amountToSend = new BigInteger(10) * new BigInteger(Math.Pow(10, 18));

        public Form1()
        {
            InitializeComponent();
            web3 = new Web3(url: web3Url);
            tokenContract = web3.Eth.GetContract(tokenContractAbi, tokenContractAddress);
            wrapperContract = web3.Eth.GetContract(wrapperContractAbi, wrapperContractAddress);
        }

        private async void WithWrapperContract_Click(object sender, EventArgs e)
        {
            ResetProgress();
            var accounts = await web3.Eth.Accounts.SendRequestAsync();
            var transferFunction = tokenContract.GetFunction("transfer");
            var approveFunction = tokenContract.GetFunction("approve");
            var balanceOfFunction = tokenContract.GetFunction("balanceOf");

            // Transfer coins to wrapper contract

            var genericGas = await transferFunction.EstimateGasAsync(wrapperContractAddress, amountToSend * 500);
            await transferFunction.SendTransactionAndWaitForReceiptAsync(accounts[0], genericGas, null, null, wrapperContractAddress, amountToSend * 500);

            var balance = await balanceOfFunction.CallAsync<BigInteger>(new object[] { wrapperContractAddress });

            // Reset & Give allowance to wrapper contract

            genericGas = await approveFunction.EstimateGasAsync(wrapperContractAddress, new BigInteger(0));
            await approveFunction.SendTransactionAndWaitForReceiptAsync(accounts[0], genericGas, null, null, wrapperContractAddress, new BigInteger(0));

            genericGas = await approveFunction.EstimateGasAsync(wrapperContractAddress, amountToSend * 500);
            await approveFunction.SendTransactionAndWaitForReceiptAsync(accounts[0], genericGas, null, null, wrapperContractAddress, amountToSend * 500);

            // Call wrapper contract transaction
            
            var transactionFunction = wrapperContract.GetFunction("transaction");
            var transactionFromFunction = wrapperContract.GetFunction("transactionFrom");
            
            var gasTF = await transactionFunction.EstimateGasAsync(null);
            var transactionFunctionReceipt = await transactionFunction.SendTransactionAndWaitForReceiptAsync(accounts[0], gasTF, null, null);
            
            StatusProgressBar.Value += 30;
            StatusTextBox.Text = "transaction function";

            Console.WriteLine("Bulk Transfer: " + transactionFunctionReceipt.CumulativeGasUsed);

            // Call wrapper contract transaction from

            var gasTFF = await transactionFromFunction.EstimateGasAsync(accounts[0]);
            var transactionFromFunctionReceipt = await transactionFromFunction.SendTransactionAndWaitForReceiptAsync(accounts[0], gasTFF, null, null, functionInput: new object[] { accounts[0] });

            StatusProgressBar.Value += 10;
            StatusTextBox.Text = "transaction from function";

            //Console.WriteLine("Bulk Transfer From: " + transactionFromFunctionReceipt.CumulativeGasUsed);

            EndProgress();
        }

        private async void NativeBulk_Click(object sender, EventArgs e)
        {
            // All calls here will be made with accounts[1]

            ResetProgress();
            var accounts = await web3.Eth.Accounts.SendRequestAsync();

            // Call bulk transfer contract

            var bulkTransferFunction = tokenContract.GetFunction("bulkTransfer");
            var bulkTransferFromFunction = tokenContract.GetFunction("bulkTransferFrom");
            var approveFunction = tokenContract.GetFunction("approve");

            // Reset & Give allowance

            var genericGas = await approveFunction.EstimateGasAsync(accounts[0], new BigInteger(0));
            await approveFunction.SendTransactionAndWaitForReceiptAsync(accounts[0], genericGas, null, null, accounts[0], new BigInteger(0));
            genericGas = await approveFunction.EstimateGasAsync(accounts[0], amountToSend * 500);
            await approveFunction.SendTransactionAndWaitForReceiptAsync(accounts[0], genericGas, null, null, accounts[0], amountToSend * 500);

            // <_to, _value, _from>
            var bulkTransferInput = new List<Tuple<string, BigInteger, string>>();
            for(var i =0; i<iterations; i++)
            {
                foreach(var randomAddress in randomAddresses)
                {
                    bulkTransferInput.Add(Tuple.Create(randomAddress, amountToSend, accounts[0]));
                }
            }
            
            var gasA = await bulkTransferFunction.EstimateGasAsync(bulkTransferInput.Select(x => x.Item1).ToArray(), bulkTransferInput.Select(x => x.Item2).ToArray());
            var receiptA = await bulkTransferFunction.SendTransactionAndWaitForReceiptAsync(accounts[0], gasA, null, null, functionInput: new object[] { bulkTransferInput.Select(x => x.Item1).ToArray(), bulkTransferInput.Select(x => x.Item2).ToArray() });
            var cumulativeGasBulkTransfer = receiptA.CumulativeGasUsed;
            Console.WriteLine("Bulk Transfer : " + cumulativeGasBulkTransfer);

            var gasB = await bulkTransferFromFunction.EstimateGasAsync(bulkTransferInput.Select(x => x.Item3).ToArray(), bulkTransferInput.Select(x => x.Item1).ToArray(), bulkTransferInput.Select(x => x.Item2).ToArray());
            var receiptB = await bulkTransferFromFunction.SendTransactionAndWaitForReceiptAsync(accounts[0], gasB, null, null, functionInput: new object[] { bulkTransferInput.Select(x => x.Item3).ToArray(), bulkTransferInput.Select(x => x.Item1).ToArray(), bulkTransferInput.Select(x => x.Item2).ToArray() });
            var cumulativeGasBulkTransferFrom = receiptB.CumulativeGasUsed;
            Console.WriteLine("Bulk Transfer From : " + cumulativeGasBulkTransferFrom);

            EndProgress();
        }

        #region Private Functions

        private void ResetProgress()
        {
            StatusProgressBar.Value = 0;
            StatusTextBox.Text = "Starting";
        }

        private void EndProgress()
        {
            StatusProgressBar.Value = 100;
            StatusTextBox.Text = "Completed";
        }

